import React, { useEffect ,useRef} from 'react'
import Blog1 from '../../assets/images/blog1.jpeg'
import Tag from '../../assets/images/label.png'
import Time from '../../assets/images/clock.png'
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation } from 'swiper/modules';
import { Mousewheel, Pagination } from 'swiper/modules';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/pagination';



export default function Blog() {

  const sliderRef = useRef(null);

  useEffect(() => {
    const handleScroll = (event) => {
      event.preventDefault(); // Prevent default page scrolling
      const delta = Math.max(-1, Math.min(1, event.deltaY)); // Get scroll direction
      const scrollStep = 300; // Set the scroll step in pixels

      const sliderContainer = sliderRef.current;
      sliderContainer.scrollBy({
        top: delta * scrollStep,
        behavior: 'smooth',
      });
    };

    // Add event listener to window scroll
    window.addEventListener('wheel', handleScroll, { passive: false });

    // Clean up the event listener when the component is unmounted
    return () => {
      window.removeEventListener('wheel', handleScroll);
    };
  }, []);

  return (
    <div ref={sliderRef}>
      <section class="blog__area no-pb blog__animation">
        <div class="container g-0 line pt-30 pb-100">
          <span class="line-3"></span>
          <div class="row">
            <div class="col-xxl-12 col-xl-12 col-lg-12 col-md-12">
              <div class="sec-title-wrapper">
                {/* <h2 class="sec-sub-title">recent blog</h2> */}
                <h3 class="sec-title">Blog</h3>
              </div>
            </div>
          </div>

          <Swiper
          // loop={true}
          mousewheel={true}
          navigation={true}
          slidesPerView={1} 
          spaceBetween={10}
           pagination={{ clickable: true, }} 
           breakpoints={{
            '@0.00': {
              slidesPerView: 1, spaceBetween: 10,
            }, '@0.75': { slidesPerView: 2, spaceBetween: 20, }, '@1.00': {

              slidesPerView: 2, spaceBetween: 40,
