import React, { useEffect, useState, useRef } from 'react';
import Home from './Component/Home/Home';
import Footer from './Component/common/Footer';
import Header from './Component/common/Header';

function App() {
  const [mouse, setMouse] = useState({ x: -100, y: -100 });
  const [pos, setPos] = useState({ x: 0, y: 0 });

  const cursorRef = useRef(null);
  const cursorCircleRef = useRef(null);

  useEffect(() => {
    const updateCoordinates = (e) => {
      setMouse({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', updateCoordinates);

    return () => {
      window.removeEventListener('mousemove', updateCoordinates);
    };
  }, []);

  useEffect(() => {
    const getAngle = (diffX, diffY) => {
      return (Math.atan2(diffY, diffX) * 180) / Math.PI;
    };

    const getSqueeze = (diffX, diffY) => {
      const distance = Math.sqrt(diffX ** 2 + diffY ** 2);
      const maxSqueeze = 0.15;
      const accelerator = 1500;
      return Math.min(distance / accelerator, maxSqueeze);
    };

    const updateCursor = () => {
      const diffX = Math.round(mouse.x - pos.x);
      const diffY = Math.round(mouse.y - pos.y);

      setPos((prevPos) => ({
        x: prevPos.x + diffX * speed,
        y: prevPos.y + diffY * speed,
      }));

      const angle = getAngle(diffX, diffY);
      const squeeze = getSqueeze(diffX, diffY);

      const scale = `scale(${1 + squeeze}, ${1 - squeeze})`;
      const rotate = `rotate(${angle}deg)`;
      const translate = `translate3d(${pos.x}px, ${pos.y}px, 0)`;

      cursorRef.current.style.transform = translate;
      cursorCircleRef.current.style.transform = rotate + scale;
    };

    const loop = () => {
      updateCursor();
      requestAnimationFrame(loop);
    };

    const cursorModifiers = document.querySelectorAll('[cursor-class]');

    cursorModifiers.forEach((cursorModifier) => {
      cursorModifier.addEventListener('mouseenter', function () {
        const className = this.getAttribute('cursor-class');
        cursorRef.current.classList.add(className);
      });

      cursorModifier.addEventListener('mouseleave', function () {
        const className = this.getAttribute('cursor-class');
        cursorRef.current.classList.remove(className);
      });
    });

    const speed = 0.1;

    requestAnimationFrame(loop);

    return () => {
      cursorModifiers.forEach((cursorModifier) => {
        cursorModifier.removeEventListener('mouseenter', () => {});
        cursorModifier.removeEventListener('mouseleave', () => {});
      });
    };
  }, [mouse, pos]);

  return (
    <div className="App">
      <div id="cursor" ref={cursorRef}>
        <div className="cursor__circle" ref={cursorCircleRef}></div>
      </div>
      <Header />
      <Home />
      <Footer />
    </div>
  );
}

export default App;
